import React, { useState } from "react";

const FilterCheckboxesActor = ({ filters, onFilterChange }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleUpClick = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? filters.length - 1 : prevIndex - 1
    );
  };

  const handleDownClick = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === filters.length - 1 ? 0 : prevIndex + 1
    );
  };

  return (
    <div className="FilterActor">
      <h3 className="subtitle">ACTOR</h3>
      <button className="up" id="upButton" onClick={handleUpClick}>
        ▲
      </button>
      <div className="carousel-container" id="containerActor">
        <div id="carouselActor" className="carousel">
          {filters.map((filter, index) => (
            <div
              className={`carousel-item ${
                index === currentIndex ? "active" : ""
              }`}
              key={filter}
            >
              <label>
                <input
                  type="checkbox"
                  value={filter}
                  onChange={(e) => onFilterChange(filter, e.target.checked)}
                />
                {filter}
              </label>
            </div>
          ))}
        </div>
      </div>
      <button className="down" id="downButton" onClick={handleDownClick}>
        ▼
      </button>
    </div>
  );
};

export default FilterCheckboxesActor;
