//Home.js
import React from "react";
import Controller from "../components/Controller";
import MovieList from "../components/MovieList";

const Home = ({
  decades,
  characters,
  keywords,
  actors,
  directors,
  sources,
  scenarios,
  adaptations,
  commentary,
  activeFilters,
  handleFilterChange,
  handleReset,
  handleSizeChange,
  textSize,
  movies,
}) => {
  return (
    <>
      <Controller
        decades={decades}
        characters={characters}
        keywords={keywords}
        actors={actors}
        directors={directors}
        sources={sources}
        scenarios={scenarios}
        adaptations={adaptations}
        commentary={commentary}
        activeFilters={activeFilters}
        handleFilterChange={handleFilterChange}
        handleReset={handleReset}
        handleSizeChange={handleSizeChange}
        textSize={textSize}
      />

      <section className="List">
        <div>
          <MovieList
            movies={movies}
            textSize={textSize}
            isDOSGothic={isDOSGothic}
          />
        </div>
      </section>
    </>
  );
};

export default Home;
