import React from "react";

const DesignEditor = () => {
  //  사이즈 조율 - - - - - - - //
  var inputSize = document.getElementById("input-size");
  var dialogueElements = document.querySelectorAll(".dialogue");
  var otherElements = document.querySelectorAll(
    ".year, .movie, .actor, .director, .source, .scenario, .adaptation"
  );

  inputSize.addEventListener("input", function () {
    var textSize = parseFloat(inputSize.value);
    var lineHeight = textSize * 1.5; // 예시로 1.5배의 크기로 설정
    var lineHeightText = lineHeight + "rem";

    dialogueElements.forEach(function (dialogue) {
      dialogue.style.fontSize = textSize + "rem";
      dialogue.style.lineHeight = lineHeightText;
    });

    // Check if font size is 10rem or larger
    var isLargeFontSize = textSize >= 10;

    dialogueElements.forEach(function (dialogueElement) {
      dialogueElement.classList.toggle("widthWide", isLargeFontSize);
    });

    otherElements.forEach(function (element) {
      element.classList.toggle("absolute", isLargeFontSize);

      // Set individual left properties based on the element type
      if (element.classList.contains("year")) {
        element.style.left = isLargeFontSize ? "1%" : "";
      } else if (element.classList.contains("movie")) {
        element.style.left = isLargeFontSize ? "8.5%" : "";
      } else if (element.classList.contains("actor")) {
        element.style.left = isLargeFontSize ? "76%" : "";
      } else if (element.classList.contains("director")) {
        element.style.left = isLargeFontSize ? "81%" : "";
      } else if (element.classList.contains("source")) {
        element.style.left = isLargeFontSize ? "86%" : "";
      } else if (element.classList.contains("scenario")) {
        element.style.left = isLargeFontSize ? "91%" : "";
      } else if (element.classList.contains("adaptation")) {
        element.style.left = isLargeFontSize ? "96%" : "";
      }

      // Add more conditions for other elements as needed
    });
  });

  return (
    <section className="CtrD">
      <div className="SwitchOuter">
        <button className="BtnReset">
          🔁
          <br />
          RESET
        </button>
        <button className="BtnDarkLight">SWITCH</button>
      </div>
      <div className="DesignEditor">
        <div className="Editors">
          <h3 className="subtitle">Design Editor ⦙⦙⦙⦙⦙⦙⦙⦙⦙⦙⦙⦙⦙⦙⦙⦙⦙⦙⦙⦙⦙⦙⦙⦙</h3>
          <div className="Editor">
            <span className="Scope">ON</span>
            <input
              type="range"
              className="EditorInput"
              min="1"
              max="10"
              step="1"
            />
            <span className="Param">가장</span>
          </div>
          <div className="Editor">
            <div id="textsize">
              <div class="box-textsize">
                <label for="input-size" class="size-label">
                  10
                </label>
                <input
                  id="input-size"
                  class="slider_C"
                  type="range"
                  value="0"
                  step="0.5"
                  min="1"
                  max="20"
                />
                <div class="size-label">0</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default DesignEditor;
