//MovieList.js
import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import { Link } from "react-router-dom";
import "../css/Modal.css";
import "../css/MovieList.css";

const MovieList = ({
  movies,
  textSize,
  style1Checked,
  style2Checked,
  style3Checked,
  style4Checked,
  style5Checked,
  style6Checked,
}) => {
  const [trCount, setTrCount] = useState(0);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    calculateTrCount();
  }, [movies]);

  const calculateTrCount = () => {
    const tbodyTable = document.querySelector(".TBodyTable");

    if (tbodyTable) {
      const trCount = tbodyTable.querySelectorAll("tr").length;
      setTrCount(trCount);
    }
  };

  const openModal = (movie) => {
    setSelectedMovie(movie);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const modalStyle = {
    content: {
      background: "black",
      width: "50vw",
      height: "60vh",
      margin: "auto",
      zIndex: 1000000,
    },
    overlay: {
      backgroundColor: "rgba(0, 0, 0, 0.7)",
      zIndex: 999,
    },
  };
  const getDialogueClassName = () => {
    const baseClassName = "ListData Dialogue";
    let additionalClassName = "";
    if (style1Checked) {
      additionalClassName += " style-1";
    } else if (style2Checked) {
      additionalClassName += " style-2";
    } else if (style3Checked) {
      additionalClassName += " style-3";
    } else if (style4Checked) {
      additionalClassName += " style-4";
    } else if (style5Checked) {
      additionalClassName += " style-5";
    } else if (style6Checked) {
      additionalClassName += " style-6";
    }
    return `${baseClassName} ${additionalClassName}`;
  };
  /* * * * * * * * PRINT * * * * * * * * */
  const printModalContent = (
    style1Checked,
    style2Checked,
    style3Checked,
    style4Checked,
    style5Checked,
    style6Checked
  ) => {
    const printWindow = window.open("", "_blank");

    if (printWindow) {
      const printDocument = printWindow.document;

      const dialogueStyles = {
        fontSize: "16px",
        fontFamily: "Arial, sans-serif", // Default font-family
        // Add other default styles here
      };

      // Determine the style class based on the checked conditions
      let styleClass = "";
      if (style1Checked) {
        styleClass = "style-1";
        dialogueStyles.fontSize = "20px";
        dialogueStyles.fontFamily = '"PilseungGothic", serif';
        // Add other styles for style1
      } else if (style2Checked) {
        styleClass = "style-2";
        // Apply styles for style2
        // Add other styles for style2
      }
      // Repeat for other styles

      // Include the link to the external stylesheet
      printDocument.write('<link rel="stylesheet" href="./MovieList.css" />');

      // Include @font-face rule
      printDocument.write(`
        <style>
          @font-face {
            font-family: "PilseungGothic";
            src: url("../../../font/NeutronicHangeul-Regularv0.3.woff2") format("woff2");
            font-weight: normal;
            font-style: normal;
          }

          .PrintedDialogue {
            font-size: ${dialogueStyles.fontSize};
            font-family: ${dialogueStyles.fontFamily};
            /* Add other styles from dialogueStyles here */
          }

          .${styleClass} {
            color: red; /* Example: Change the color for style-1 */
            /* Add styles specific to the selected style class */
          }
        </style>
      `);

      printDocument.write("<html><head><title>Print</title></head><body>");
      printDocument.write("<div>");

      if (selectedMovie) {
        printDocument.write(
          `<h2 class="ModalTitle ${styleClass}">${selectedMovie.title}</h2>`
        );
        printDocument.write(
          `<p class="ModalText">Year: ${selectedMovie.year}</p>`
        );
        printDocument.write(
          `<p class="PrintedDialogue ${styleClass}">${selectedMovie.dialogue}</p>`
        );
        // Add other modal content here
      }

      printDocument.write("</div></body></html>");
      printDocument.close();

      printWindow.print();
      printWindow.onafterprint = function () {
        printWindow.close();
      };
    }
  };

  /* * * * * * * * PRINT END* * * * * * * * */
  return (
    <div className="TableContainer">
      <table className="TheadTable">
        <thead>
          <tr className="indexContainer">
            <th id="indexYear" className="index">
              연도
            </th>
            <th id="indexMovie" className="index">
              영화제목
            </th>
            <th id="indexDialogue" className="index">
              대사
            </th>
            <th id="indexActor" className="index">
              배우
            </th>
            <th id="indexDirector" className="index">
              감독
            </th>
            <th id="indexSource" className="index">
              원작
            </th>
            <th id="indexScenario" className="index">
              각본
            </th>
            <th id="indexAdaptation" className="index">
              각색
            </th>
            <th id="indexClip" className="index">
              클립영상
            </th>
          </tr>
        </thead>
      </table>
      <table className="TBodyTable">
        <tbody className="ListContainer">
          {movies.map((movie) => {
            return (
              <tr key={movie.id} onClick={() => openModal(movie)}>
                <td className="ListData Year">{movie.year}</td>
                <td className="ListData Movie">{movie.title}</td>
                <td
                  className={getDialogueClassName()}
                  style={{
                    fontSize: `${textSize}px`,
                  }}
                >
                  {movie.dialogue}
                </td>
                <td className="ListData Actor">{movie.actor}</td>
                <td className="ListData Director">{movie.director}</td>
                <td className="ListData Source">{movie.source}</td>
                <td className="ListData Scenario">{movie.scenario}</td>
                <td className="ListData Adaptation">{movie.adaptation}</td>
                <td className="ListData Clip">{movie.clip}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
      {trCount === 0 && <p className="DataResult">검색 결과가 없습니다.</p>}
      {trCount > 0 && (
        <p className="DataResult">검색 결과: {trCount} 개의 데이터</p>
      )}

      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Movie Detail Modal"
        style={modalStyle}
      >
        {selectedMovie && (
          <Link to={`/`} key={selectedMovie.id}>
            <h2 className="ModalTitle">{selectedMovie.title}</h2>
            <p className="ModalText">Year: {selectedMovie.year}</p>
            <p
              style={{
                fontSize: `${textSize}px`,
              }}
              className={getDialogueClassName()}
            >
              {selectedMovie.dialogue}
            </p>
            <p className="ModalText">{selectedMovie.actor}</p>
            <p className="ModalText">{selectedMovie.director}</p>
            <p className="ModalText">{selectedMovie.source}</p>
            <p className="ModalText">{selectedMovie.scenario}</p>
            <p className="ModalText">{selectedMovie.adaptation}</p>
            <p className="ModalText">{selectedMovie.clip}</p>
            <p className="ModalText">{selectedMovie.commentary}</p>
            <div>
              <Link to={`/`} key={selectedMovie.id}>
                {/* Modal content here */}
              </Link>
              <button onClick={printModalContent}>Print</button>
            </div>
          </Link>
        )}
        <button
          onClick={() =>
            printModalContent(
              style1Checked,
              style2Checked,
              style3Checked,
              style4Checked,
              style5Checked,
              style6Checked
            )
          }
        >
          Print
        </button>
      </Modal>
    </div>
  );
};

export default MovieList;
