import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const Detail = ({ movies }) => {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    // movies 배열에서 해당 id에 맞는 영화를 찾음
    const selectedMovie = movies.find((m) => m.id === id);
    setMovie(selectedMovie);
  }, [id, movies]);

  if (!movie) {
    return <p>영화를 찾을 수 없습니다.</p>;
  }

  return (
    <div>
      <h2>{movie.title}</h2>
      <table>
        <tbody>
          <tr>
            <td>Year</td>
            <td>{movie.year}</td>
          </tr>
          <tr>
            <td>Movie</td>
            <td>{movie.title}</td>
          </tr>
          <tr>
            <td>Dialogue</td>
            <td style={{ fontSize: "16px" }}>{movie.dialogue}</td>
          </tr>
          {/* 나머지 영화 정보에 대한 행 추가 */}
        </tbody>
      </table>
    </div>
  );
};

export default Detail;
