//Home.js
import React from "react";
import Controller from "../components/Controller";
import MovieList from "../components/MovieList";

const Home = ({
  decades,
  characters,
  keywords,
  actors,
  directors,
  sources,
  scenarios,
  adaptations,
  commentary,
  activeFilters,
  handleFilterChange,
  handleReset,
  handleSizeChange,
  textSize,
  movies,
  style1Checked,
  handleStyle1,
}) => {
  return (
    <>
      <Controller
        decades={decades}
        characters={characters}
        keywords={keywords}
        actors={actors}
        directors={directors}
        sources={sources}
        scenarios={scenarios}
        adaptations={adaptations}
        commentary={commentary}
        activeFilters={activeFilters}
        handleFilterChange={handleFilterChange}
        handleReset={handleReset}
        handleSizeChange={handleSizeChange}
        textSize={textSize}
        style1Checked={style1Checked}
        style2Checked={style2Checked}
        style3Checked={style3Checked}
        style4Checked={style4Checked}
        style5Checked={style5Checked}
        style6Checked={style6Checked}
        handleStyle1={handleStyle1}
        handleStyle2={handleStyle2}
        handleStyle3={handleStyle3}
        handleStyle4={handleStyle4}
        handleStyle5={handleStyle5}
        handleStyle6={handleStyle6}
      />

      <section className="List">
        <div>
          <MovieList
            movies={movies}
            textSize={textSize}
            style1Checked={style1Checked}
            style2Checked={style2Checked}
            style3Checked={style3Checked}
            style4Checked={style4Checked}
            style5Checked={style5Checked}
            style6Checked={style6Checked}
            handleStyle1={handleStyle1}
            handleStyle2={handleStyle2}
            handleStyle3={handleStyle3}
            handleStyle4={handleStyle4}
            handleStyle5={handleStyle5}
            handleStyle5={handleStyle6}
          />
        </div>
      </section>
    </>
  );
};

export default Home;
