import React, { useState, useEffect } from "react";

const MovieList = ({ movies, textSize }) => {
  const [trCount, setTrCount] = useState(0);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    calculateTrCount();
  }, [movies]);

  const calculateTrCount = () => {
    const tbodyTable = document.querySelector(".TBodyTable");

    if (tbodyTable) {
      const trCount = tbodyTable.querySelectorAll("tr").length;
      setTrCount(trCount);
    }
  };

  const handleRowClick = (movie) => {
    setSelectedMovie(movie);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div className="TableContainer">
      {/* ... (your existing code) */}
      <table className="TBodyTable">
        <tbody className="ListContainer">
          {movies.map((movie) => (
            <tr key={movie.id} onClick={() => handleRowClick(movie)}>
              {/* ... (your existing code for rendering each row) */}
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal */}
      {isModalOpen && (
        <div className="Modal">
          <div className="ModalContent">
            <span className="CloseButton" onClick={closeModal}>
              &times;
            </span>
            <p className="ModalData Actor">{selectedMovie.actor}</p>
            <p className="ModalData Director">{selectedMovie.director}</p>
            <p className="ModalData Source">{selectedMovie.source}</p>
            <p className="ModalData Scenario">{selectedMovie.scenario}</p>
            <p className="ModalData Adaptation">{selectedMovie.adaptation}</p>
            <p className="ModalData Clip">{selectedMovie.clip}</p>
            <p className="ModalData Commentary">{selectedMovie.commentary}</p>
          </div>
        </div>
      )}

      {/* ... (your existing code for displaying result count) */}
    </div>
  );
};

export default MovieList;
