// src/components/FilterCheckboxes.js
import React from "react";

const FilterCheckboxesActor = ({ filters, onFilterChange }) => {
  var Carousel = {
    width: 50,
    numVisible: 8,
    duration: 500,
    padding: 2,
    rowHeight: 30,
    visibleRows: 7, // 한 번에 이동할 행 수
  };

  function rotateForward(carousel) {
    var children = carousel.children;
    for (var i = 0; i < Carousel.visibleRows; i++) {
      var firstChild = children[0];
      var lastChild = children[children.length - 1];
      carousel.insertBefore(lastChild, firstChild);
    }
  }

  function rotateBackward(carousel) {
    var children = carousel.children;
    for (var i = 0; i < Carousel.visibleRows; i++) {
      var firstChild = children[0];
      var lastChild = children[children.length - 1];
      carousel.insertBefore(firstChild, lastChild.nextSibling);
    }
  }

  function animate(carousel, begin, end, finalTask) {
    var change = end - begin;
    var duration = Carousel.duration;
    var startTime = Date.now();
    carousel.style.top = begin + "px";
    var animateInterval = window.setInterval(function () {
      var t = Date.now() - startTime;
      if (t >= duration) {
        window.clearInterval(animateInterval);
        finalTask();
        return;
      }
      t /= duration / 2;
      var top =
        begin +
        (t < 1
          ? (change / 2) * Math.pow(t, 3)
          : (change / 2) * (Math.pow(t - 2, 3) + 2));
      carousel.style.top = top + "px";
    }, 1000 / 60);
  }

  function setupCarousel(containerId, carouselId, downButtonId, upButtonId) {
    var container = document.getElementById(containerId);
    var carousel = document.getElementById(carouselId);
    var downButton = document.getElementById(downButtonId);
    var upButton = document.getElementById(upButtonId);
    downButton.onClick = function () {
      rotateForward(carousel);
      animate(
        carousel,
        -Carousel.rowHeight * Carousel.visibleRows,
        0,
        function () {}
      );
    };

    upButton.onClick = function () {
      animate(
        carousel,
        0,
        -Carousel.rowHeight * Carousel.visibleRows,
        function () {
          rotateBackward(carousel);
          carousel.style.top = "0";
        }
      );
    };
  }

  window.onload = function () {
    setupCarousel("containerActor", "carouselActor", "downActor", "upActor");
    setupCarousel(
      "containerDirector",
      "carouselDirector",
      "downDirector",
      "upDirector"
    );
    setupCarousel(
      "containerSource",
      "carouselSource",
      "downSource",
      "upSource"
    );
    setupCarousel(
      "containerScenario",
      "carouselScenario",
      "downScenario",
      "upScenario"
    );
    setupCarousel("containerAdapt", "carouselAdapt", "downAdapt", "upAdapt");
  };

  return (
    <div className="FilterActor">
      <h3 className="subtitle">ACTOR</h3>
      <button className="up" id="upActor">
        {" "}
        {/* Corrected ID */}▲
      </button>
      <div className="carousel-container" id="containerActor">
        <div id="carouselActor" className="carousel">
          {filters.map((filter) => (
            <div className="carousel-item" key={filter}>
              <label>
                <input
                  type="checkbox"
                  value={filter}
                  onChange={(e) => onFilterChange(filter, e.target.checked)}
                />
                {filter}
              </label>
            </div>
          ))}
        </div>
      </div>
      <button className="down" id="downActor">
        {" "}
        {/* Corrected ID */}▼
      </button>
    </div>
  );
};

export default FilterCheckboxesActor;
