import React, { useState } from "react";
import "./FilterActor.css"; // 스타일 파일을 가져옴

const FilterActor = ({ filters }) => {
  const [selectedFilters, setSelectedFilters] = useState([]);

  const handleFilterChange = (filter, checked) => {
    if (checked) {
      setSelectedFilters([...selectedFilters, filter]);
    } else {
      setSelectedFilters(
        selectedFilters.filter((selectedFilter) => selectedFilter !== filter)
      );
    }
  };

  return (
    <div className="FilterActor">
      <div className="carousel">
        {filters.map((filter, index) => (
          <div
            className={`carousel-item ${
              selectedFilters.includes(filter) ? "selected" : ""
            }`}
            key={filter}
            onClick={() =>
              handleFilterChange(filter, !selectedFilters.includes(filter))
            }
          >
            <label htmlFor={`ActorInput${index}`}>
              <input type="checkbox" id={`ActorInput${index}`} />
              <span className="carousel-text">{filter}</span>
            </label>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FilterActor;
